SELECT COUNT(D.DOC_ID) AS CNT FROM XR_DOCUMENT D INNER JOIN XR_FILED F ON D.DOC_ID = F.DOC_ID INNER JOIN XR_PAGE P ON F.PAGE_ID = P.PAGE_ID INNER JOIN XR_TYPE T ON D.DOC_TYPE = T.TYPE_ID
AND P.PAGE_NAME = #{page_name}
AND P.PAGE_SIZE = #{file_size}
AND D.IS_CURRENT = #{is_current} AND D.DOC_STATUS = 'C'
AND D.CREATE_DATE >= TO_DATE(#{sdate},'YYYY-MM-DD')
AND D.CREATE_DATE < TO_DATE(#{edate},'YYYY-MM-DD')+0.99999

SELECT DOC_ID,ROOT_ID,DOC_NAME, TYPE_NAME, IS_LOCKED, VERSION_NO, CREATOR_ID,DOC_STATUS,DOC_TYPE, CREATOR_NAME, CREATE_DATE FROM ( SELECT D.DOC_ID, D.ROOT_ID,D.DOC_NAME,D.IS_LOCKED, D.VERSION_NO,D.CREATOR_ID,D.DOC_STATUS, D.CREATOR_NAME, D.CREATE_DATE,T.TYPE_NAME,T.TYPE_ID AS DOC_TYPE, CEIL((ROW_NUMBER() OVER(ORDER BY ${orderCol} ${orderType} )) / ${page_size} ) PAGENUM FROM XR_DOCUMENT D INNER JOIN XR_FILED F ON D.DOC_ID = F.DOC_ID INNER JOIN XR_PAGE P ON F.PAGE_ID = P.PAGE_ID INNER JOIN XR_TYPE T ON D.DOC_TYPE = T.TYPE_ID
AND P.PAGE_NAME = #{page_name}
AND P.PAGE_SIZE = #{file_size}
AND D.IS_CURRENT = #{is_current} AND D.DOC_STATUS = 'C'
AND D.CREATE_DATE >= TO_DATE(#{sdate},'YYYY-MM-DD')
AND D.CREATE_DATE < TO_DATE(#{edate},'YYYY-MM-DD')+0.99999
) LIST WHERE LIST.PAGENUM = #{nPage}

