mapper.select:
SELECT DOC_ID, DOC_NAME, DOC_TYPE, ROOT_ID, CREATOR_NAME, WASTE_DATE,IS_LOCKED, CREATE_DATE, TYPE_NAME, OWNER_NAME,PAGENUM FROM ( SELECT D.DOC_ID, D.DOC_NAME, D.DOC_TYPE, D.ROOT_ID, D.CREATOR_NAME, D.CREATE_DATE,D.OWNER_ID,D.WASTE_DATE, COALESCE((SELECT TYPE_NAME FROM XR_TYPE WHERE TYPE_ID = D.DOC_TYPE),'Deleted') AS TYPE_NAME, U.USER_NAME_KO AS OWNER_NAME, D.IS_LOCKED, CEIL((ROW_NUMBER() OVER(ORDER BY ${orderCol} ${orderType} )) / ${page_size} ) PAGENUM FROM XR_DOCUMENT_DEL D INNER JOIN XR_USER U ON D.OWNER_ID = U.USER_ID
INNER JOIN XR_GROUPED G ON U.USER_ID =G.USER_ID
D.IS_CURRENT = 'T'
AND D.WASTE_DATE >= TO_DATE(#{sdate},'YYYY-MM-DD')
AND D.WASTE_DATE < TO_DATE(#{edate},'YYYY-MM-DD')+0.99999
AND D.DOC_NAME LIKE '%${strKeyword}%'
AND U.USER_NAME_KO LIKE '%${ownerKeyword}%'
AND G.GROUP_ID IN
#{item}
)LIST WHERE PAGENUM = #{nPage}

SELECT DOC_ID, DOC_NAME, DOC_TYPE, TYPE_NAME, VERSION_NO, VERSION_NOTE, ROOT_ID, CREATOR_ID, CREATOR_NAME, CREATE_DATE, UPDATER_ID, UPDATER_NAME, UPDATE_DATE, EXPIRED_DATE FROM ( SELECT D.DOC_ID, D.DOC_NAME, D.DOC_TYPE, T.TYPE_NAME, D.VERSION_NO, D.VERSION_NOTE, D.ROOT_ID, D.CREATOR_ID, D.CREATOR_NAME, D.UPDATER_ID, D.UPDATER_NAME, D.CREATE_DATE, D.UPDATE_DATE, D.EXPIRED_DATE FROM XR_DOCUMENT_DEL D INNER JOIN XR_TYPE T ON D.DOC_TYPE = T.TYPE_ID
DOC_ID =#{doc_id}
) LIST

