mapper.select:
SELECT DOC_ID, DOC_NAME, DOC_TYPE, CREATOR_NAME, EXPIRED_DATE, ROOT_ID,TYPE_NAME, OWNER_NAME,PAGENUM FROM ( SELECT D.DOC_ID, D.DOC_NAME, D.DOC_TYPE, D.ROOT_ID, D.CREATOR_NAME, TO_CHAR(D.EXPIRED_DATE,'YYYY-MM-DD') AS EXPIRED_DATE,T.TYPE_NAME,U.USER_NAME_KO AS OWNER_NAME, CEIL((ROW_NUMBER() OVER(ORDER BY ${orderCol} ${orderType} )) / ${page_size} ) PAGENUM FROM XR_DOCUMENT D INNER JOIN XR_TYPE T ON D.DOC_TYPE = T.TYPE_ID INNER JOIN XR_USER U ON D.OWNER_ID = U.USER_ID
INNER JOIN XR_GROUPED G ON U.USER_ID =G.USER_ID
WHERE D.IS_EXPIRED = 'T' AND D.IS_CURRENT = 'T' AND D.DOC_STATUS='C'
AND D.DOC_NAME LIKE '%${strKeyword}%'
AND U.USER_NAME_KO LIKE '%${ownerKeyword}%'
AND D.EXPIRED_DATE >= TO_DATE(#{sdate},'YYYY-MM-DD')
AND D.EXPIRED_DATE < TO_DATE(#{edate},'YYYY-MM-DD')+0.99999
AND G.GROUP_ID IN
#{item}
) LIST WHERE LIST.PAGENUM = #{nPage}

