SELECT 
	COUNT(*) AS CNT
FROM XR_DOCUMENT D 
	INNER JOIN XR_LINKED L ON D.DOC_ID = L.DOC_ID
	INNER JOIN XR_FOLDER F ON L.FOLDER_ID = F.FOLDER_ID

	
		AND F.FOLDER_ID = #{folder_id}
			
	
		AND D.DOC_ID != #{doc_id}
	
	
		AND D.DOC_NAME = #{doc_name}
					
	
		AND D.IS_CURRENT = #{is_current}
	
	
		AND D.DOC_STATUS = #{doc_status}

